import React, { useState, useEffect } from "react";
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Navigate,
} from "react-router-dom";

import Navbar from "./components/Navbar";
import Home from "./pages/Home";
import About from "./pages/About";
import Reports from "./pages/Reports";
import ProjectDetail from "./pages/ProjectDetail";
import Login from "./pages/Login";
import SubmitReport from "./pages/SubmitReport";
import RegisterDiaspora from "./pages/RegisterDiaspora";
import Admin from "./pages/Admin";

import { getUserRole } from "./utils/auth";
import bg from "./assets/bg.jpg"; // ✅ Background image

function App() {
  const [userRole, setUserRole] = useState(null);

  useEffect(() => {
    const role = getUserRole();
    setUserRole(role);
  }, []);

  return (
    <div
      className="min-h-screen bg-cover bg-center bg-fixed"
      style={{ backgroundImage: `url(${bg})` }}
    >
      <div className="min-h-screen backdrop-blur-sm bg-white/80">
        <Router>
          <Navbar userRole={userRole} />
          <main className="min-h-[calc(100vh-64px)]">
            <Routes>
              <Route path="/" element={<Home />} />
              <Route path="/about" element={<About />} />
              <Route
                path="/reports"
                element={
                  userRole === "admin" || userRole === "ngo" || userRole === "gov" ? (
                    <Reports />
                  ) : (
                    <Navigate to="/login" replace />
                  )
                }
              />
              <Route path="/projects/:id" element={<ProjectDetail />} />
              <Route
                path="/login"
                element={<Login setUserRole={setUserRole} />}
              />
              <Route path="/submit-report" element={<SubmitReport />} />
              <Route path="/register" element={<RegisterDiaspora />} />
              <Route
                path="/admin"
                element={
                  userRole === "admin" ? (
                    <Admin />
                  ) : (
                    <Navigate to="/login" replace />
                  )
                }
              />
              <Route
              path="/my-reports"
              element={
                userRole === "diaspora" ? <MyReports /> : <Navigate to="/login" replace />
              }
             />
            </Routes>
          </main>
        </Router>
      </div>
    </div>
  );
}

export default App;
