import { Link } from "react-router-dom";

const reports = [
  {
    id: "june-attack-khartoum",
    title: "June 3rd Massacre - Khartoum",
    summary: "Peaceful protest attacked by security forces. Dozens killed, many missing.",
    date: "2019-06-03",
    tags: ["Khartoum", "Massacre", "Protest"]
  },
  {
    id: "blue-nile-displacement",
    title: "Displacement in Blue Nile",
    summary: "Thousands flee due to conflict in Blue Nile state. Urgent aid needed.",
    date: "2022-11-14",
    tags: ["Blue Nile", "Displacement"]
  }
];

export default function Reports() {
  return (
    <div className="max-w-4xl mx-auto px-4 py-10">
      <h1 className="text-2xl font-bold mb-6 text-blue-600">Reports Archive</h1>
      <div className="grid gap-6">
        {reports.map((report) => (
          <Link
            key={report.id}
            to={`/reports/${report.id}`}
            className="block border rounded-lg p-4 hover:shadow transition bg-white"
          >
            <h2 className="text-lg font-semibold text-blue-700">{report.title}</h2>
            <p className="text-sm text-gray-600 mb-1">{report.summary}</p>
            <p className="text-xs text-gray-500">{report.date} â€” {report.tags.join(", ")}</p>
          </Link>
        ))}
      </div>
    </div>
  );
}
