// Signup.jsx
import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

const Signup = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    fullName: "",
    dob: "",
    email: "",
    phone: "",
    password: "",
    confirmPassword: "",
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (formData.password !== formData.confirmPassword) {
      alert("Passwords do not match");
      return;
    }

    // Simulate account creation
    const user = {
      ...formData,
      role: "user",
      verified: false,
    };

    localStorage.setItem("newUser", JSON.stringify(user));
    alert("Account created! Check your email for verification (simulated).");
    navigate("/login");
  };

  return (
    <div className="min-h-screen flex items-center justify-center px-4">
      <div className="bg-white/90 p-8 rounded-xl shadow-md w-full max-w-lg">
        <h1 className="text-3xl font-bold text-primary mb-6 text-center">Sign Up</h1>
        <form onSubmit={handleSubmit} className="space-y-4">
          <div>
            <label className="label">Full Name</label>
            <input
              type="text"
              name="fullName"
              required
              className="input"
              value={formData.fullName}
              onChange={handleChange}
            />
          </div>
          <div>
            <label className="label">Date of Birth</label>
            <input
              type="date"
              name="dob"
              required
              className="input"
              value={formData.dob}
              onChange={handleChange}
            />
          </div>
          <div>
            <label className="label">Email</label>
            <input
              type="email"
              name="email"
              required
              className="input"
              value={formData.email}
              onChange={handleChange}
            />
          </div>
          <div>
            <label className="label">Phone Number (optional)</label>
            <input
              type="tel"
              name="phone"
              className="input"
              value={formData.phone}
              onChange={handleChange}
            />
          </div>
          <div>
            <label className="label">Password</label>
            <input
              type="password"
              name="password"
              required
              className="input"
              value={formData.password}
              onChange={handleChange}
            />
          </div>
          <div>
            <label className="label">Confirm Password</label>
            <input
              type="password"
              name="confirmPassword"
              required
              className="input"
              value={formData.confirmPassword}
              onChange={handleChange}
            />
          </div>
          <button type="submit" className="btn-primary w-full">
            Create Account
          </button>
        </form>
      </div>
    </div>
  );
};

export default Signup;
