import React, { useEffect, useState } from "react";

const Admin = () => {
  const [reports, setReports] = useState([]);
  const [diaspora, setDiaspora] = useState([]);

  useEffect(() => {
    const storedReports = JSON.parse(localStorage.getItem("submittedReports")) || [];
    const storedDiaspora = JSON.parse(localStorage.getItem("registeredDiaspora")) || [];
    setReports(storedReports);
    setDiaspora(storedDiaspora);
  }, []);

  return (
    <div className="max-w-6xl mx-auto px-6 py-10">
      <h1 className="text-4xl font-bold text-blue-800 mb-8 border-b-2 pb-2">Admin Dashboard</h1>

      {/* Reports */}
      <section className="mb-12">
        <h2 className="text-2xl font-semibold text-gray-800 mb-4">Civic Reports</h2>
        {reports.length === 0 ? (
          <p className="text-gray-500 italic">No reports submitted yet.</p>
        ) : (
          <div className="grid gap-6">
            {reports.map((report, index) => (
              <div key={index} className="card border-l-4 border-blue-500 p-5 bg-white shadow-lg rounded-lg">
                <h3 className="text-lg font-bold text-blue-700 mb-1">{report.title}</h3>
                <p><span className="font-semibold">Category:</span> {report.category}</p>
                <p><span className="font-semibold">Description:</span> {report.description}</p>
                <p><span className="font-semibold">Location:</span> {report.location || "N/A"}</p>
                <p><span className="font-semibold">Anonymous:</span> {report.anonymous ? "Yes" : "No"}</p>
              </div>
            ))}
          </div>
        )}
      </section>

      {/* Diaspora */}
      <section>
        <h2 className="text-2xl font-semibold text-gray-800 mb-4">Diaspora Registrations</h2>
        {diaspora.length === 0 ? (
          <p className="text-gray-500 italic">No diaspora registrations found.</p>
        ) : (
          <div className="grid gap-6">
            {diaspora.map((person, index) => (
              <div key={index} className="card border-l-4 border-green-500 p-5 bg-white shadow-lg rounded-lg">
                <h3 className="text-lg font-bold text-green-700 mb-1">{person.fullName}</h3>
                <p><strong>Email:</strong> {person.email}</p>
                <p><strong>Country:</strong> {person.country}</p>
                <p><strong>Profession:</strong> {person.profession}</p>
                <p><strong>Expertise:</strong> {person.expertise}</p>
                <p><strong>Availability:</strong> {person.availability}</p>
              </div>
            ))}
          </div>
        )}
      </section>
    </div>
  );
};

export default Admin;
