import { Link } from "react-router-dom";

const projects = [
  {
    id: "clean-up",
    title: "Neighborhood Clean-Up",
    summary: "Empowering locals to revitalize public spaces.",
  },
  {
    id: "youth-tech",
    title: "Youth Tech Mentorship",
    summary: "Free digital literacy and coding workshops for youth.",
  },
  {
    id: "food-solidarity",
    title: "Food Solidarity Drive",
    summary: "Distributing essential supplies to low-income families.",
  },
];

export default function Home() {
  return (
    <div className="bg-gray-100 min-h-screen pb-20">
      <div className="text-center py-12">
        <h1 className="text-3xl font-bold text-blue-600">Welcome to Alkarama Hub</h1>
        <p className="text-gray-600 mt-2">Grassroots solutions. Community power.</p>
      </div>

      <div className="max-w-5xl mx-auto px-4">
        <h2 className="text-xl font-semibold text-gray-700 mb-6">Featured Projects</h2>
        <div className="grid md:grid-cols-3 gap-6">
          {projects.map((project) => (
            <Link
              key={project.id}
              to={`/projects/${project.id}`}
              className="block bg-white p-4 shadow rounded hover:shadow-lg transition"
            >
              <h3 className="text-lg font-bold text-blue-600">{project.title}</h3>
              <p className="text-gray-600 mt-2 text-sm">{project.summary}</p>
              <p className="text-sm text-blue-500 mt-3">Learn more â†’</p>
            </Link>
          ))}
        </div>
      </div>
    </div>
  );
}
