import React, { useEffect, useState } from "react";

const DiasporaProfile = () => {
  const [profile, setProfile] = useState(null);

  useEffect(() => {
    const all = JSON.parse(localStorage.getItem("registeredDiaspora")) || [];
    const current = all[all.length - 1]; // Assume last registered user for now
    setProfile(current);
  }, []);

  if (!profile) {
    return (
      <div className="min-h-screen flex items-center justify-center px-4">
        <p className="text-gray-600 italic">No profile data found.</p>
      </div>
    );
  }

  return (
    <div className="max-w-3xl mx-auto py-16 px-4">
      <div className="bg-white/90 p-8 shadow-xl rounded-xl">
        <h1 className="text-3xl font-bold text-primary mb-6 text-center">Your Profile</h1>

        <div className="space-y-4 text-lg">
          <p><strong>Name:</strong> {profile.fullName}</p>
          <p><strong>Email:</strong> {profile.email}</p>
          <p><strong>Country:</strong> {profile.country}</p>
          <p><strong>Profession:</strong> {profile.profession}</p>
          <p><strong>Expertise:</strong> {profile.expertise}</p>
          <p><strong>Availability:</strong> {profile.availability}</p>
        </div>
      </div>
    </div>
  );
};

export default DiasporaProfile;
