import React, { useState } from "react";

const DiasporaRegister = () => {
  const [formData, setFormData] = useState({
    fullName: "",
    email: "",
    country: "",
    profession: "",
    expertise: "",
    availability: "part-time",
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("Diaspora Registration Data:", formData);
    alert("Thank you for registering!");
    setFormData({
      fullName: "",
      email: "",
      country: "",
      profession: "",
      expertise: "",
      availability: "part-time",
    });
  };

  return (
    <div className="max-w-3xl mx-auto p-6">
      <h1 className="text-2xl font-bold text-blue-700 mb-4">Diaspora Registration</h1>
      <form onSubmit={handleSubmit} className="space-y-4 bg-white p-6 shadow rounded">
        <div>
          <label className="block font-semibold">Full Name</label>
          <input
            type="text"
            name="fullName"
            required
            value={formData.fullName}
            onChange={handleChange}
            className="w-full border px-3 py-2 rounded"
          />
        </div>
        <div>
          <label className="block font-semibold">Email</label>
          <input
            type="email"
            name="email"
            required
            value={formData.email}
            onChange={handleChange}
            className="w-full border px-3 py-2 rounded"
          />
        </div>
        <div>
          <label className="block font-semibold">Country of Residence</label>
          <input
            type="text"
            name="country"
            required
            value={formData.country}
            onChange={handleChange}
            className="w-full border px-3 py-2 rounded"
          />
        </div>
        <div>
          <label className="block font-semibold">Profession</label>
          <input
            type="text"
            name="profession"
            required
            value={formData.profession}
            onChange={handleChange}
            className="w-full border px-3 py-2 rounded"
          />
        </div>
        <div>
          <label className="block font-semibold">Areas of Expertise</label>
          <textarea
            name="expertise"
            required
            value={formData.expertise}
            onChange={handleChange}
            className="w-full border px-3 py-2 rounded"
          />
        </div>
        <div>
          <label className="block font-semibold">Availability</label>
          <select
            name="availability"
            value={formData.availability}
            onChange={handleChange}
            className="w-full border px-3 py-2 rounded"
          >
            <option value="full-time">Full-time</option>
            <option value="part-time">Part-time</option>
            <option value="ad-hoc">Ad-hoc</option>
          </select>
        </div>
        <button
          type="submit"
          className="bg-green-700 text-white px-4 py-2 rounded hover:bg-green-800"
        >
          Register
        </button>
      </form>
    </div>
  );
};

export default DiasporaRegister;
