import React, { useEffect, useState } from "react";

const AdminProjects = () => {
  const [projects, setProjects] = useState([]);
  const [form, setForm] = useState({
    id: "",
    title: "",
    summary: "",
  });
  const [isEditing, setIsEditing] = useState(false);

  useEffect(() => {
    const saved = JSON.parse(localStorage.getItem("projects")) || [];
    setProjects(saved);
  }, []);

  const saveToStorage = (list) => {
    localStorage.setItem("projects", JSON.stringify(list));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const updated = [...projects];

    if (isEditing) {
      const idx = updated.findIndex((p) => p.id === form.id);
      if (idx !== -1) updated[idx] = form;
    } else {
      updated.push({ ...form, id: Date.now().toString() });
    }

    setProjects(updated);
    saveToStorage(updated);
    resetForm();
  };

  const resetForm = () => {
    setForm({ id: "", title: "", summary: "" });
    setIsEditing(false);
  };

  const handleEdit = (project) => {
    setForm(project);
    setIsEditing(true);
  };

  const handleDelete = (id) => {
    const filtered = projects.filter((p) => p.id !== id);
    setProjects(filtered);
    saveToStorage(filtered);
    resetForm();
  };

  return (
    <div>
      <h2 className="text-2xl font-semibold text-textDark mb-6">Rebuilding Projects</h2>

      {/* Form */}
      <form onSubmit={handleSubmit} className="bg-white p-6 rounded-xl shadow mb-8 space-y-4">
        <input
          type="text"
          placeholder="Project Title"
          value={form.title}
          onChange={(e) => setForm({ ...form, title: e.target.value })}
          required
          className="input"
        />
        <textarea
          placeholder="Short summary"
          value={form.summary}
          onChange={(e) => setForm({ ...form, summary: e.target.value })}
          required
          className="textarea"
        />
        <div className="flex gap-4">
          <button type="submit" className="btn-primary">
            {isEditing ? "Update Project" : "Add Project"}
          </button>
          {isEditing && (
            <button
              type="button"
              onClick={resetForm}
              className="btn-secondary"
            >
              Cancel
            </button>
          )}
        </div>
      </form>

      {/* Project List */}
      <div className="grid gap-6">
        {projects.map((project) => (
          <div
            key={project.id}
            className="card border-l-4 border-primary bg-white/95"
          >
            <h3 className="text-lg font-bold text-primary">{project.title}</h3>
            <p className="text-gray-700 mt-1">{project.summary}</p>
            <div className="mt-3 flex gap-4">
              <button
                onClick={() => handleEdit(project)}
                className="text-sm text-green-700 hover:underline"
              >
                Edit
              </button>
              <button
                onClick={() => handleDelete(project.id)}
                className="text-sm text-red-600 hover:underline"
              >
                Delete
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default AdminProjects;
