import React, { useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";

const Navbar = () => {
  const [role, setRole] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const savedRole = localStorage.getItem("userRole");
    setRole(savedRole);
  }, []);

  const handleLogout = () => {
    localStorage.removeItem("userRole");
    setRole(null);
    navigate("/");
  };

  return (
    <nav className="bg-white shadow sticky top-0 z-50">
      <div className="max-w-7xl mx-auto px-6 py-4 flex justify-between items-center">
        <Link to="/" className="text-2xl font-extrabold text-blue-800 tracking-tight hover:text-blue-700 transition">
          Alkarama Hub
        </Link>
        <ul className="flex flex-wrap space-x-4 text-sm font-medium items-center">
          <li>
            <Link to="/" className="text-gray-700 hover:text-blue-600 transition-colors duration-200">Home</Link>
          </li>
          <li>
            <Link to="/reports" className="text-gray-700 hover:text-blue-600 transition-colors duration-200">Reports</Link>
          </li>
          <li>
            <Link to="/about" className="text-gray-700 hover:text-blue-600 transition-colors duration-200">About</Link>
          </li>
          <li>
            <Link to="/submit-report" className="text-gray-700 hover:text-blue-600 transition-colors duration-200">
              Submit Report
            </Link>
          </li>
          <li>
            <Link to="/register" className="text-gray-700 hover:text-blue-600 transition-colors duration-200">
              Register (Diaspora)
            </Link>
          </li>
          {role === "admin" && (
            <li>
              <Link to="/admin" className="text-gray-700 hover:text-blue-600 transition-colors duration-200">
                Admin
              </Link>
            </li>
          )}
          {!role ? (
            <li>
              <Link to="/login" className="text-blue-600 hover:underline transition">Login</Link>
            </li>
          ) : (
            <li>
              <button
                onClick={handleLogout}
                className="text-red-600 hover:underline transition"
              >
                Logout
              </button>
            </li>
          )}
        </ul>
      </div>
    </nav>
  );
};

export default Navbar;
