import React, { useState } from "react";
import { useNavigate, Link } from "react-router-dom";
import { login } from "../utils/auth";

const Login = ({ setUserRole }) => {
  const navigate = useNavigate();
  const [form, setForm] = useState({ email: "", password: "" });
  const [error, setError] = useState("");

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleLogin = (e) => {
    e.preventDefault();
    const users = JSON.parse(localStorage.getItem("registeredUsers")) || [];
    const found = users.find(
      (u) => u.email === form.email && u.password === form.password
    );

    if (!found) {
      setError("Invalid email or password.");
      return;
    }

    login(found.role);
    setUserRole(found.role);
    localStorage.setItem("userEmail", found.email);
    navigate(found.role === "admin" ? "/admin" : "/");
  };

  return (
    <div className="min-h-screen flex items-center justify-center px-4">
      <div className="bg-white/90 p-8 rounded-xl shadow-md w-full max-w-md">
        <h1 className="text-3xl font-bold text-primary mb-6 text-center">Login</h1>

        {error && <p className="text-red-600 text-sm mb-4">{error}</p>}

        <form onSubmit={handleLogin} className="space-y-5">
          <div>
            <label className="block font-semibold mb-1">Email</label>
            <input
              type="email"
              name="email"
              value={form.email}
              onChange={handleChange}
              required
              className="input"
            />
          </div>

          <div>
            <label className="block font-semibold mb-1">Password</label>
            <input
              type="password"
              name="password"
              value={form.password}
              onChange={handleChange}
              required
              className="input"
            />
          </div>

          <div className="text-right text-sm text-blue-700 hover:underline">
            <Link to="/forgot-password">Forgot password?</Link>
          </div>

          <button type="submit" className="btn-primary w-full">
            Login
          </button>

          <p className="text-sm text-center text-gray-600 mt-4">
            Donâ€™t have an account?{" "}
            <Link to="/register" className="text-primary hover:underline">
              Sign up
            </Link>
          </p>
        </form>
      </div>
    </div>
  );
};

export default Login;
