import React from "react";
import { Link } from "react-router-dom";

const reports = [
  {
    id: 1,
    title: "Displacement in El Geneina",
    summary: "Over 100,000 people have been displaced due to escalating violence.",
    date: "June 2025",
  },
  {
    id: 2,
    title: "Access to Water in Nyala",
    summary: "Field reports show critical shortages in clean water in South Darfur.",
    date: "May 2025",
  },
  {
    id: 3,
    title: "Attacks on Civilian Hospitals",
    summary: "Hospitals in Khartoum and Omdurman are being targeted by armed forces.",
    date: "April 2025",
  },
];

const Reports = () => {
  return (
    <div className="max-w-5xl mx-auto p-6">
      <h1 className="text-2xl font-bold text-blue-700 mb-6">Situation Reports</h1>
      <div className="space-y-4">
        {reports.map((report) => (
          <div key={report.id} className="bg-white p-4 shadow rounded">
            <h2 className="text-lg font-semibold">{report.title}</h2>
            <p className="text-sm text-gray-600">{report.date}</p>
            <p className="text-gray-700 mt-2">{report.summary}</p>
            <Link to={`/reports/${report.id}`} className="text-blue-600 hover:underline mt-2 inline-block">
              Read more â†’
            </Link>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Reports;
