import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";

const Projects = () => {
  const [projects, setProjects] = useState([]);

  useEffect(() => {
    const saved = JSON.parse(localStorage.getItem("projects")) || [];
    setProjects(saved);
  }, []);

  return (
    <div className="max-w-5xl mx-auto py-20 px-4">
      <h1 className="text-4xl font-extrabold text-primary mb-10 text-center">
        Rebuilding Projects
      </h1>
      {projects.length === 0 ? (
        <p className="text-center text-gray-500 italic">No projects added yet.</p>
      ) : (
        <div className="grid gap-8 md:grid-cols-2">
          {projects.map((project) => (
            <div
              key={project.id}
              className="bg-white/90 p-6 rounded-xl shadow hover:shadow-lg border-l-4 border-primary"
            >
              <h2 className="text-xl font-bold text-primary mb-2">{project.title}</h2>
              <p className="text-textDark mb-4">{project.summary}</p>
              <Link
                to={`/projects/${project.id}`}
                className="text-sm font-medium text-secondary hover:underline"
              >
                View Details â†’
              </Link>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default Projects;
