import { useState } from "react";

export default function Home() {
  const projects = [
    {
      title: "Community Clean-Up",
      short: "Neighborhood clean-up efforts.",
      full: "We're organizing volunteers to clean local parks, streets, and shared spaces across the city every Saturday morning.",
    },
    {
      title: "Youth Coding Bootcamp",
      short: "Programming for teens.",
      full: "A 6-week bootcamp teaching JavaScript, HTML, and CSS to empower the next generation of web developers.",
    },
    {
      title: "Food Distribution Drive",
      short: "Helping families in need.",
      full: "Partnering with local farms and stores to distribute food parcels to underprivileged households every weekend.",
    },
  ];

  const [expanded, setExpanded] = useState(null);

  const toggle = (index) => {
    setExpanded(expanded === index ? null : index);
  };

  return (
    <div className="bg-gray-100 min-h-screen pb-20">
      <div className="text-center py-12">
        <h1 className="text-3xl font-bold text-blue-600">Welcome to Alkarama Hub</h1>
        <p className="text-gray-600 mt-2">Empowering Communities</p>
      </div>

      <div className="max-w-5xl mx-auto px-4">
        <h2 className="text-xl font-semibold text-gray-700 mb-6">Our Projects</h2>
        <div className="grid md:grid-cols-3 gap-6">
          {projects.map((project, index) => (
            <div
              key={index}
              className="bg-white p-4 shadow rounded hover:shadow-lg transition flex flex-col"
            >
              <h3 className="text-lg font-bold text-blue-600">{project.title}</h3>
              <p className="text-gray-600 mt-2 text-sm">{project.short}</p>

              {expanded === index && (
                <p className="text-gray-500 text-sm mt-2">{project.full}</p>
              )}

              <button
                className="mt-auto text-sm text-blue-500 hover:underline mt-3"
                onClick={() => toggle(index)}
              >
                {expanded === index ? "Show Less" : "Show More"}
              </button>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}
