import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import Navbar from "./components/Navbar";
import Home from "./pages/Home";
import Reports from "./pages/Reports";
import About from "./pages/About";
import RegisterDiaspora from "./pages/RegisterDiaspora";
import SubmitReport from "./pages/SubmitReport";
import AdminDashboard from "./pages/AdminDashboard";
import Login from "./pages/Login";
import ProtectedRoute from "./components/ProtectedRoute";
import { useState } from "react";
import bg from "./assets/bg.jpg"; // ✅ import your background image

function App() {
  const [userRole, setUserRole] = useState(null);

  return (
    <div
      className="min-h-screen bg-cover bg-center bg-fixed"
      style={{ backgroundImage: `url(${bg})` }}
    >
      <div className="backdrop-blur-sm bg-white/80 min-h-screen">
        <Router>
          <Navbar />
          <Routes>
            <Route path="/" element={<Home />} />
            <Route path="/reports" element={<Reports />} />
            <Route path="/about" element={<About />} />
            <Route path="/register" element={<RegisterDiaspora />} />
            <Route path="/login" element={<Login setUserRole={setUserRole} />} />
            <Route
              path="/submit-report"
              element={
                <ProtectedRoute role="diaspora">
                  <SubmitReport />
                </ProtectedRoute>
              }
            />
            <Route
              path="/admin"
              element={
                <ProtectedRoute role="admin">
                  <AdminDashboard />
                </ProtectedRoute>
              }
            />
          </Routes>
        </Router>
      </div>
    </div>
  );
}

export default App;
