import { login } from "../utils/auth";
import { useNavigate } from "react-router-dom";

const Login = ({ setUserRole }) => {
  const navigate = useNavigate();

  const handleLogin = (role) => {
    login(role);
    setUserRole(role);
    if (role === "admin") navigate("/admin");
    else navigate("/");
  };

  return (
    <div className="min-h-screen flex items-center justify-center px-4">
      <div className="bg-white/90 p-8 rounded-xl shadow-md w-full max-w-md text-center">
        <h1 className="text-3xl font-bold text-primary mb-6">Login</h1>
        <p className="text-textDark mb-8">Select your role to log in and access the platform.</p>
        <div className="space-y-4">
          <button
            onClick={() => handleLogin("admin")}
            className="w-full bg-primary hover:bg-green-700 text-white py-3 rounded-lg font-semibold transition duration-300"
          >
            Login as Admin
          </button>
          <button
            onClick={() => handleLogin("diaspora")}
            className="w-full bg-secondary hover:bg-red-700 text-white py-3 rounded-lg font-semibold transition duration-300"
          >
            Login as Diaspora
          </button>
        </div>
      </div>
    </div>
  );
};

export default Login;
