import React, { useState } from "react";

const mockReports = [
  {
    id: 1,
    title: "Displacement in El Geneina",
    date: "June 2025",
    status: "Pending",
  },
  {
    id: 2,
    title: "Water Shortages in Nyala",
    date: "May 2025",
    status: "Pending",
  },
];

const mockDiaspora = [
  {
    id: 1,
    name: "Sara Ibrahim",
    profession: "Civil Engineer",
    country: "UK",
  },
  {
    id: 2,
    name: "Omar Musa",
    profession: "Doctor",
    country: "Qatar",
  },
];

const AdminDashboard = () => {
  const [reports, setReports] = useState(mockReports);
  const [diaspora, setDiaspora] = useState(mockDiaspora);

  const resolveReport = (id) => {
    setReports((prev) =>
      prev.map((r) => (r.id === id ? { ...r, status: "Resolved" } : r))
    );
  };

  return (
    <div className="max-w-5xl mx-auto p-6">
      <h1 className="text-2xl font-bold text-blue-700 mb-6">Admin Dashboard</h1>

      <section className="mb-10">
        <h2 className="text-xl font-semibold mb-4">Civic Reports</h2>
        <div className="space-y-3">
          {reports.map((r) => (
            <div key={r.id} className="bg-white shadow p-4 rounded flex justify-between">
              <div>
                <h3 className="font-bold">{r.title}</h3>
                <p className="text-sm text-gray-600">{r.date}</p>
                <p>Status: <span className="font-medium">{r.status}</span></p>
              </div>
              <button
                onClick={() => resolveReport(r.id)}
                className="text-white bg-green-600 px-3 py-1 rounded hover:bg-green-700"
                disabled={r.status === "Resolved"}
              >
                {r.status === "Resolved" ? "Resolved" : "Mark Resolved"}
              </button>
            </div>
          ))}
        </div>
      </section>

      <section>
        <h2 className="text-xl font-semibold mb-4">Diaspora Registrations</h2>
        <div className="space-y-3">
          {diaspora.map((d) => (
            <div key={d.id} className="bg-white shadow p-4 rounded">
              <p><strong>Name:</strong> {d.name}</p>
              <p><strong>Profession:</strong> {d.profession}</p>
              <p><strong>Country:</strong> {d.country}</p>
            </div>
          ))}
        </div>
      </section>
    </div>
  );
};

export default AdminDashboard;
