import React, { useEffect, useState } from "react";

const Admin = () => {
  const [reports, setReports] = useState([]);
  const [diaspora, setDiaspora] = useState([]);

  useEffect(() => {
    // Simulate loading reports
    const storedReports = JSON.parse(localStorage.getItem("submittedReports")) || [];
    const storedDiaspora = JSON.parse(localStorage.getItem("registeredDiaspora")) || [];
    setReports(storedReports);
    setDiaspora(storedDiaspora);
  }, []);

  return (
    <div className="max-w-6xl mx-auto p-6">
      <h1 className="text-2xl font-bold text-blue-700 mb-6">Admin Dashboard</h1>

      <div className="mb-8">
        <h2 className="text-xl font-semibold mb-4">Civic Reports</h2>
        {reports.length === 0 ? (
          <p className="text-gray-600">No reports submitted.</p>
        ) : (
          <div className="space-y-4">
            {reports.map((report, index) => (
              <div key={index} className="border p-4 rounded bg-white shadow">
                <h3 className="font-semibold">{report.title}</h3>
                <p><strong>Category:</strong> {report.category}</p>
                <p><strong>Description:</strong> {report.description}</p>
                <p><strong>Location:</strong> {report.location || "N/A"}</p>
                <p><strong>Anonymous:</strong> {report.anonymous ? "Yes" : "No"}</p>
              </div>
            ))}
          </div>
        )}
      </div>

      <div>
        <h2 className="text-xl font-semibold mb-4">Diaspora Registrations</h2>
        {diaspora.length === 0 ? (
          <p className="text-gray-600">No diaspora registrations found.</p>
        ) : (
          <div className="space-y-4">
            {diaspora.map((person, index) => (
              <div key={index} className="border p-4 rounded bg-white shadow">
                <h3 className="font-semibold">{person.fullName}</h3>
                <p><strong>Email:</strong> {person.email}</p>
                <p><strong>Country:</strong> {person.country}</p>
                <p><strong>Profession:</strong> {person.profession}</p>
                <p><strong>Expertise:</strong> {person.expertise}</p>
                <p><strong>Availability:</strong> {person.availability}</p>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default Admin;
