import { login } from "../utils/auth";
import { useNavigate } from "react-router-dom";

const Login = () => {
  const navigate = useNavigate();

  const handleLogin = (role) => {
    login(role);
    if (role === "admin") navigate("/admin");
    else navigate("/");
  };

  return (
    <div className="p-6">
      <h1 className="text-xl font-bold mb-4">Login</h1>
      <button onClick={() => handleLogin("admin")} className="mr-4 bg-blue-600 text-white px-4 py-2 rounded">Login as Admin</button>
      <button onClick={() => handleLogin("diaspora")} className="bg-green-600 text-white px-4 py-2 rounded">Login as Diaspora</button>
    </div>
  );
};

export default Login;
