import { Link } from "react-router-dom";

const projects = [
  {
    id: "clean-up",
    title: "Neighborhood Clean-Up",
    summary: "Empowering locals to revitalize public spaces.",
  },
  {
    id: "youth-tech",
    title: "Youth Tech Mentorship",
    summary: "Free digital literacy and coding workshops for youth.",
  },
  {
    id: "food-solidarity",
    title: "Food Solidarity Drive",
    summary: "Distributing essential supplies to low-income families.",
  },
];

export default function Home() {
  return (
    <div className="bg-gradient-to-br from-blue-50 to-green-50 min-h-screen pb-20">
      <div className="text-center py-16">
        <h1 className="text-4xl font-extrabold text-blue-800">Welcome to Alkarama Hub</h1>
        <p className="text-gray-700 mt-4 text-lg">Grassroots Solutions. Diaspora Impact. Community Power.</p>
      </div>

      <div className="max-w-6xl mx-auto px-4">
        <h2 className="text-2xl font-semibold text-gray-800 mb-6 border-b pb-2">Featured Projects</h2>
        <div className="grid gap-6 md:grid-cols-3">
          {projects.map((project) => (
            <Link
              key={project.id}
              to={`/projects/${project.id}`}
              className="bg-white shadow hover:shadow-xl transition p-6 rounded-lg border-l-4 border-blue-500"
            >
              <h3 className="text-lg font-bold text-blue-700">{project.title}</h3>
              <p className="text-gray-600 mt-2">{project.summary}</p>
              <span className="text-blue-500 mt-4 inline-block font-medium">Learn more â†’</span>
            </Link>
          ))}
        </div>
      </div>
    </div>
  );
}
