import React, { useState } from "react";
import { useNavigate, Link } from "react-router-dom";

const Login = ({ setUserRole }) => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({ email: "", password: "" });
  const [error, setError] = useState("");
  const [unverifiedUser, setUnverifiedUser] = useState(null);

  const handleChange = (e) => {
    setFormData((prev) => ({ ...prev, [e.target.name]: e.target.value }));
  };

  const handleLogin = (e) => {
    e.preventDefault();
    const users = JSON.parse(localStorage.getItem("registeredUsers")) || [];
    const foundUser = users.find((u) => u.email === formData.email);

    if (!foundUser || foundUser.password !== formData.password) {
      setError("Invalid credentials.");
      return;
    }

    if (!foundUser.verified) {
      setUnverifiedUser(foundUser);
      return;
    }

    // login success
    localStorage.setItem("userRole", foundUser.role);
    localStorage.setItem("userEmail", foundUser.email);
    setUserRole(foundUser.role);
    navigate(foundUser.role === "admin" ? "/admin" : "/");
  };

  const handleVerify = () => {
    const users = JSON.parse(localStorage.getItem("registeredUsers")) || [];
    const updated = users.map((u) =>
      u.email === unverifiedUser.email ? { ...u, verified: true } : u
    );
    localStorage.setItem("registeredUsers", JSON.stringify(updated));
    setUnverifiedUser(null);
    alert("Email verified! You can now log in.");
  };

  return (
    <div className="min-h-screen flex items-center justify-center px-4">
      <div className="bg-white/90 p-8 rounded-xl shadow-md w-full max-w-md text-center">
        <h1 className="text-3xl font-bold text-primary mb-6">Login</h1>

        {unverifiedUser ? (
          <>
            <p className="text-red-600 font-medium mb-4">
              Please verify your email to proceed.
            </p>
            <button
              onClick={handleVerify}
              className="btn-primary w-full"
            >
              Verify Now
            </button>
          </>
        ) : (
          <>
            <form onSubmit={handleLogin} className="space-y-4 text-left">
              <div>
                <label className="label">Email</label>
                <input
                  type="email"
                  name="email"
                  value={formData.email}
                  onChange={handleChange}
                  className="input"
                  required
                />
              </div>
              <div>
                <label className="label">Password</label>
                <input
                  type="password"
                  name="password"
                  value={formData.password}
                  onChange={handleChange}
                  className="input"
                  required
                />
              </div>
              {error && <p className="text-red-500 text-sm">{error}</p>}
              <button type="submit" className="btn-primary w-full">
                Log In
              </button>
            </form>

            <div className="mt-4 text-sm text-gray-600">
              <Link to="/forgot-password" className="text-primary hover:underline">
                Forgot Password?
              </Link>
            </div>
            <div className="mt-2 text-sm text-gray-600">
              Don't have an account?{" "}
              <Link to="/register" className="text-primary hover:underline">
                Sign Up
              </Link>
            </div>
          </>
        )}
      </div>
    </div>
  );
};

export default Login;
