import React, { useState } from "react";

const SubmitReport = () => {
  const [formData, setFormData] = useState({
    title: "",
    description: "",
    category: "corruption",
    location: "",
    anonymous: false,
  });

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: type === "checkbox" ? checked : value,
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("Submitted Report:", formData);
    alert("Report submitted successfully!");
    setFormData({
      title: "",
      description: "",
      category: "corruption",
      location: "",
      anonymous: false,
    });
  };

  return (
    <div className="max-w-3xl mx-auto p-6">
      <h1 className="text-2xl font-bold text-blue-700 mb-4">Submit a Civic Report</h1>
      <form onSubmit={handleSubmit} className="space-y-4 bg-white p-6 shadow rounded">
        <div>
          <label className="block font-semibold">Report Title</label>
          <input
            type="text"
            name="title"
            value={formData.title}
            onChange={handleChange}
            required
            className="w-full border px-3 py-2 rounded"
          />
        </div>
        <div>
          <label className="block font-semibold">Description</label>
          <textarea
            name="description"
            value={formData.description}
            onChange={handleChange}
            required
            className="w-full border px-3 py-2 rounded"
          />
        </div>
        <div>
          <label className="block font-semibold">Category</label>
          <select
            name="category"
            value={formData.category}
            onChange={handleChange}
            className="w-full border px-3 py-2 rounded"
          >
            <option value="corruption">Corruption</option>
            <option value="medical">Medical Emergency</option>
            <option value="infrastructure">Infrastructure</option>
            <option value="security">Security Threat</option>
            <option value="other">Other</option>
          </select>
        </div>
        <div>
          <label className="block font-semibold">Location (optional)</label>
          <input
            type="text"
            name="location"
            value={formData.location}
            onChange={handleChange}
            className="w-full border px-3 py-2 rounded"
          />
        </div>
        <div className="flex items-center">
          <input
            type="checkbox"
            name="anonymous"
            checked={formData.anonymous}
            onChange={handleChange}
            className="mr-2"
          />
          <label>Submit anonymously</label>
        </div>
        <button
          type="submit"
          className="bg-blue-700 text-white px-4 py-2 rounded hover:bg-blue-800"
        >
          Submit Report
        </button>
      </form>
    </div>
  );
};

export default SubmitReport;
