import React, { useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";

const Navbar = () => {
  const [role, setRole] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const savedRole = localStorage.getItem("userRole");
    setRole(savedRole);
  }, []);

  const handleLogout = () => {
    localStorage.removeItem("userRole");
    setRole(null);
    navigate("/");
  };

  return (
    <nav className="bg-white/80 backdrop-blur-md shadow-md sticky top-0 z-50">
      <div className="max-w-7xl mx-auto px-6 py-4 flex justify-between items-center">
        <Link
          to="/"
          className="text-2xl font-extrabold text-primary tracking-wide hover:opacity-80 transition"
        >
          Alkarama Hub
        </Link>
        <ul className="flex flex-wrap space-x-4 text-sm font-medium items-center">
          <li>
            <Link to="/" className="text-textDark hover:text-primary transition">
              Home
            </Link>
          </li>
          <li>
            <Link to="/reports" className="text-textDark hover:text-primary transition">
              Reports
            </Link>
          </li>
          <li>
            <Link to="/about" className="text-textDark hover:text-primary transition">
              About
            </Link>
          </li>
          {role === "diaspora" && (
            <li>
              <Link
                to="/submit-report"
                className="text-textDark hover:text-primary transition"
              >
                Submit Report
              </Link>
            </li>
          )}
          <li>
            <Link
              to="/register"
              className="text-textDark hover:text-primary transition"
            >
              Register (Diaspora)
            </Link>
          </li>
          {role === "admin" && (
            <li>
              <Link
                to="/admin"
                className="text-textDark hover:text-primary transition"
              >
                Admin
              </Link>
            </li>
          )}
          {!role ? (
            <li>
              <Link
                to="/login"
                className="text-primary hover:underline transition"
              >
                Login
              </Link>
            </li>
          ) : (
            <li>
              <button
                onClick={handleLogout}
                className="text-secondary hover:underline transition"
              >
                Logout
              </button>
            </li>
          )}
        </ul>
      </div>
    </nav>
  );
};

export default Navbar;
