import { useParams } from "react-router-dom";

const projectDetails = {
  "clean-up": {
    title: "Neighborhood Clean-Up",
    description:
      "Alkarama Hub partners with local groups to organize bi-weekly clean-ups in underserved areas. We provide tools, coordinate volunteers, and promote collective responsibility.",
  },
  "youth-tech": {
    title: "Youth Tech Mentorship",
    description:
      "This program introduces youth (ages 12–18) to basic web development, digital literacy, and project collaboration. Led by volunteer developers and educators.",
  },
  "food-solidarity": {
    title: "Food Solidarity Drive",
    description:
      "Through partnerships with food banks and donors, we deliver packages of fresh produce and dry goods to 250+ families monthly. Volunteers help pack and distribute.",
  },
};

export default function ProjectDetail() {
  const { id } = useParams();
  const project = projectDetails[id];

  if (!project) {
    return (
      <div className="min-h-screen flex items-center justify-center px-4">
        <div className="bg-white/90 p-8 rounded-lg shadow text-center text-red-600 text-lg font-semibold">
          Project not found.
        </div>
      </div>
    );
  }

  return (
    <div className="max-w-3xl mx-auto py-20 px-4">
      <div className="bg-white/90 shadow-xl rounded-xl p-10">
        <h1 className="text-3xl font-extrabold text-primary mb-4">{project.title}</h1>
        <p className="text-textDark text-lg leading-relaxed">{project.description}</p>
      </div>
    </div>
  );
}
