import React, { useEffect, useState } from "react";

const DiasporaProfile = () => {
  const storedEmail = localStorage.getItem("userEmail") || "";
  const [formData, setFormData] = useState({
    fullName: "",
    dob: "",
    country: "",
    phone: "",
    profession: "",
    expertise: "",
    availability: "part-time",
    profilePic: null,
    cvFile: null,
    email: storedEmail,
  });

  useEffect(() => {
    const storedUsers = JSON.parse(localStorage.getItem("users")) || [];
    const user = storedUsers.find((u) => u.email === storedEmail);
    if (user) setFormData({ ...formData, ...user });
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleFileChange = (e) => {
    const { name, files } = e.target;
    if (files.length > 0) {
      const file = files[0];
      const reader = new FileReader();
      reader.onloadend = () => {
        setFormData((prev) => ({ ...prev, [name]: reader.result }));
      };
      reader.readAsDataURL(file);
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const users = JSON.parse(localStorage.getItem("users")) || [];
    const updated = users.map((user) =>
      user.email === storedEmail ? { ...user, ...formData } : user
    );
    localStorage.setItem("users", JSON.stringify(updated));
    alert("Profile updated successfully!");
  };

  return (
    <div className="max-w-3xl mx-auto py-16 px-4">
      <div className="bg-white/90 p-8 shadow-xl rounded-xl">
        <h1 className="text-3xl font-bold text-primary mb-6 text-center">
          My Profile
        </h1>
        <form onSubmit={handleSubmit} className="space-y-5">
          <div>
            <label className="label">Full Name</label>
            <input
              name="fullName"
              value={formData.fullName}
              onChange={handleChange}
              className="input"
              required
            />
          </div>
          <div>
            <label className="label">Date of Birth</label>
            <input
              type="date"
              name="dob"
              value={formData.dob}
              onChange={handleChange}
              className="input"
              required
            />
          </div>
          <div>
            <label className="label">Country</label>
            <input
              name="country"
              value={formData.country}
              onChange={handleChange}
              className="input"
              required
            />
          </div>
          <div>
            <label className="label">Phone (optional)</label>
            <input
              name="phone"
              value={formData.phone}
              onChange={handleChange}
              className="input"
            />
          </div>
          <div>
            <label className="label">Profession</label>
            <input
              name="profession"
              value={formData.profession}
              onChange={handleChange}
              className="input"
              required
            />
          </div>
          <div>
            <label className="label">Areas of Expertise</label>
            <textarea
              name="expertise"
              value={formData.expertise}
              onChange={handleChange}
              className="textarea"
              rows={3}
            />
          </div>
          <div>
            <label className="label">Availability</label>
            <select
              name="availability"
              value={formData.availability}
              onChange={handleChange}
              className="input"
            >
              <option value="full-time">Full-time</option>
              <option value="part-time">Part-time</option>
              <option value="ad-hoc">Ad-hoc</option>
            </select>
          </div>
          <div>
            <label className="label">Profile Picture</label>
            <input
              type="file"
              accept="image/*"
              name="profilePic"
              onChange={handleFileChange}
              className="input"
            />
            {formData.profilePic && (
              <img
                src={formData.profilePic}
                alt="Preview"
                className="w-24 h-24 mt-2 rounded-full object-cover"
              />
            )}
          </div>
          <div>
            <label className="label">Upload CV (PDF)</label>
            <input
              type="file"
              accept="application/pdf"
              name="cvFile"
              onChange={handleFileChange}
              className="input"
            />
            {formData.cvFile && (
              <p className="text-sm text-green-700 mt-2">CV uploaded âœ”</p>
            )}
          </div>
          <button type="submit" className="btn-primary w-full">
            Save Profile
          </button>
        </form>
      </div>
    </div>
  );
};

export default DiasporaProfile;
