import React, { useState } from "react";

const RegisterDiaspora = () => {
  const [formData, setFormData] = useState({
    fullName: "",
    email: "",
    country: "",
    profession: "",
    expertise: "",
    availability: "part-time",
  });

  const [errors, setErrors] = useState({});

  const validate = () => {
    const newErrors = {};

    if (!formData.fullName.trim()) newErrors.fullName = "Full name is required.";
    if (!formData.email.trim()) newErrors.email = "Email is required.";
    else if (!/\S+@\S+\.\S+/.test(formData.email)) newErrors.email = "Email is invalid.";

    if (!formData.country.trim()) newErrors.country = "Country is required.";
    if (!formData.profession.trim()) newErrors.profession = "Profession is required.";
    if (!formData.expertise.trim()) newErrors.expertise = "Expertise is required.";
    else if (formData.expertise.length > 300)
      newErrors.expertise = "Expertise must be under 300 characters.";

    return newErrors;
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const validationErrors = validate();
    if (Object.keys(validationErrors).length > 0) {
      setErrors(validationErrors);
      return;
    }

    alert("Thank you for registering!");
    console.log("Diaspora Registration Data:", formData);

    setFormData({
      fullName: "",
      email: "",
      country: "",
      profession: "",
      expertise: "",
      availability: "part-time",
    });
    setErrors({});
  };

  return (
    <div className="min-h-screen py-16 px-4">
      <div className="max-w-3xl mx-auto bg-white/90 p-8 shadow-xl rounded-xl">
        <h1 className="text-3xl font-bold text-primary mb-6 text-center">
          Diaspora Registration
        </h1>
        <form onSubmit={handleSubmit} className="space-y-5">
          {/* Full Name */}
          <div>
            <label className="label">Full Name</label>
            <input
              type="text"
              name="fullName"
              value={formData.fullName}
              onChange={handleChange}
              className="input"
            />
            {errors.fullName && <p className="text-red-600 text-sm mt-1">{errors.fullName}</p>}
          </div>

          {/* Email */}
          <div>
            <label className="label">Email</label>
            <input
              type="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              className="input"
            />
            {errors.email && <p className="text-red-600 text-sm mt-1">{errors.email}</p>}
          </div>

          {/* Country */}
          <div>
            <label className="label">Country of Residence</label>
            <input
              type="text"
              name="country"
              value={formData.country}
              onChange={handleChange}
              className="input"
            />
            {errors.country && <p className="text-red-600 text-sm mt-1">{errors.country}</p>}
          </div>

          {/* Profession */}
          <div>
            <label className="label">Profession</label>
            <input
              type="text"
              name="profession"
              value={formData.profession}
              onChange={handleChange}
              className="input"
            />
            {errors.profession && <p className="text-red-600 text-sm mt-1">{errors.profession}</p>}
          </div>

          {/* Expertise */}
          <div>
            <label className="label">Areas of Expertise</label>
            <textarea
              name="expertise"
              value={formData.expertise}
              onChange={handleChange}
              className="textarea"
              rows={4}
            />
            {errors.expertise && <p className="text-red-600 text-sm mt-1">{errors.expertise}</p>}
          </div>

          {/* Availability */}
          <div>
            <label className="label">Availability</label>
            <select
              name="availability"
              value={formData.availability}
              onChange={handleChange}
              className="input"
            >
              <option value="full-time">Full-time</option>
              <option value="part-time">Part-time</option>
              <option value="ad-hoc">Ad-hoc</option>
            </select>
          </div>

          <button type="submit" className="btn-primary w-full">
            Register
          </button>
        </form>
      </div>
    </div>
  );
};

export default RegisterDiaspora;
