import React from "react";
import { Link, useNavigate } from "react-router-dom";

const Navbar = ({ userRole }) => {
  const navigate = useNavigate();

  const handleLogout = () => {
    localStorage.removeItem("userRole");
    localStorage.removeItem("userEmail");
    navigate("/");
    window.location.reload();
  };

  return (
    <nav className="bg-white/80 backdrop-blur-md shadow-md sticky top-0 z-50">
      <div className="max-w-7xl mx-auto px-6 py-4 flex justify-between items-center">
        <Link
          to="/"
          className="text-2xl font-extrabold text-primary tracking-wide hover:opacity-80 transition"
        >
          Alkarama Hub
        </Link>
        <ul className="flex flex-wrap space-x-4 text-sm font-medium items-center">
          <li>
            <Link to="/" className="text-textDark hover:text-primary transition">
              Home
            </Link>
          </li>
          <li>
            <Link to="/about" className="text-textDark hover:text-primary transition">
              About
            </Link>
          </li>
          {userRole === "diaspora" && (
            <>
              <li>
                <Link to="/submit-report" className="text-textDark hover:text-primary transition">
                  Submit Report
                </Link>
              </li>
              <li>
                <Link to="/my-reports" className="text-textDark hover:text-primary transition">
                  My Reports
                </Link>
              </li>
              <li>
                <Link to="/profile" className="text-textDark hover:text-primary transition">
                  My Profile
                </Link>
              </li>
            </>
          )}
          {userRole === "admin" && (
            <li>
              <Link to="/admin" className="text-textDark hover:text-primary transition">
                Admin
              </Link>
            </li>
          )}
          {!userRole && (
            <li>
              <Link to="/login" className="text-primary hover:underline transition">
                Login
              </Link>
            </li>
          )}
          <li>
            <Link to="/register" className="text-textDark hover:text-primary transition">
              Register
            </Link>
          </li>
          {userRole && (
            <li>
              <button
                onClick={handleLogout}
                className="text-secondary hover:underline transition"
              >
                Logout
              </button>
            </li>
          )}
        </ul>
      </div>
    </nav>
  );
};

export default Navbar;
