import { Link } from "react-router-dom";

const projects = [
  {
    id: "clean-up",
    title: "Neighborhood Clean-Up",
    summary: "Empowering locals to revitalize public spaces.",
  },
  {
    id: "youth-tech",
    title: "Youth Tech Mentorship",
    summary: "Free digital literacy and coding workshops for youth.",
  },
  {
    id: "food-solidarity",
    title: "Food Solidarity Drive",
    summary: "Distributing essential supplies to low-income families.",
  },
];

export default function Home() {
  return (
    <div className="bg-gradient-to-br from-blue-50 via-purple-50 to-yellow-50 min-h-screen pb-20">
      <div className="text-center py-20 px-4">
        <h1 className="text-5xl font-extrabold text-blue-800 tracking-tight mb-4">
          Welcome to Alkarama Hub
        </h1>
        <p className="text-gray-700 text-xl max-w-2xl mx-auto">
          Grassroots Solutions. Diaspora Impact. Community Power.
        </p>
      </div>

      <div className="max-w-6xl mx-auto px-6">
        <h2 className="text-2xl font-semibold text-gray-800 mb-8 border-b pb-2">
          Featured Projects
        </h2>
        <div className="grid gap-8 md:grid-cols-3">
          {projects.map((project) => (
            <Link
              key={project.id}
              to={`/projects/${project.id}`}
              className="bg-white rounded-xl shadow-md hover:shadow-2xl hover:scale-[1.03] transition-all duration-300 border-l-4 border-blue-500 p-6"
            >
              <h3 className="text-lg font-bold text-blue-700 mb-2">
                {project.title}
              </h3>
              <p className="text-gray-600">{project.summary}</p>
              <span className="text-blue-500 mt-4 inline-block font-medium hover:underline">
                Learn more â†’
              </span>
            </Link>
          ))}
        </div>
      </div>
    </div>
  );
}
