import React, { useEffect, useState } from "react";

const AdminReports = () => {
  const [reports, setReports] = useState([]);

  useEffect(() => {
    const storedReports = JSON.parse(localStorage.getItem("submittedReports")) || [];
    setReports(storedReports);
  }, []);

  const toggleStatus = (index) => {
    const updated = [...reports];
    const current = updated[index].status || "Pending";
    updated[index].status = current === "Resolved" ? "Pending" : "Resolved";
    setReports(updated);
    localStorage.setItem("submittedReports", JSON.stringify(updated));
  };

  return (
    <div>
      <h2 className="text-2xl font-semibold text-textDark mb-4">Civic Reports</h2>
      {reports.length === 0 ? (
        <p className="text-gray-500 italic">No reports submitted yet.</p>
      ) : (
        <div className="grid gap-6">
          {reports.map((report, index) => (
            <div
              key={index}
              className="card border-l-4 border-primary bg-white/95 shadow-lg rounded-lg"
            >
              <h3 className="text-lg font-bold text-primary mb-1">{report.title}</h3>
              <p><strong>Status:</strong> {report.status || "Pending"}</p>
              <p><strong>Category:</strong> {report.category}</p>
              <p><strong>Description:</strong> {report.description}</p>
              <p><strong>Location:</strong> {report.location || "N/A"}</p>
              <p><strong>Anonymous:</strong> {report.anonymous ? "Yes" : "No"}</p>
              <button
                onClick={() => toggleStatus(index)}
                className="mt-2 btn-secondary text-sm"
              >
                Toggle Status
              </button>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default AdminReports;
