import React, { useEffect, useState } from "react";

const AdminUsers = () => {
  const [users, setUsers] = useState([]);

  useEffect(() => {
    const storedUsers = JSON.parse(localStorage.getItem("registeredUsers")) || [];
    setUsers(storedUsers);
  }, []);

  const handleDelete = (email) => {
    const updated = users.filter((u) => u.email !== email);
    setUsers(updated);
    localStorage.setItem("registeredUsers", JSON.stringify(updated));
  };

  const handleToggleRole = (email) => {
    const updated = users.map((u) =>
      u.email === email ? { ...u, role: u.role === "diaspora" ? "ngo" : "diaspora" } : u
    );
    setUsers(updated);
    localStorage.setItem("registeredUsers", JSON.stringify(updated));
  };

  return (
    <div className="bg-white/90 shadow p-6 rounded-xl">
      <h2 className="text-2xl font-bold mb-4">Manage Users</h2>
      {users.length === 0 ? (
        <p className="text-gray-500">No registered users.</p>
      ) : (
        <div className="space-y-4">
          {users.map((user) => (
            <div
              key={user.email}
              className="border border-gray-300 rounded-lg p-4 shadow flex flex-col md:flex-row md:items-center md:justify-between"
            >
              <div>
                <p className="font-semibold">{user.fullName}</p>
                <p className="text-sm text-gray-600">{user.email}</p>
                <p className="text-sm text-gray-600">Role: {user.role}</p>
              </div>
              <div className="mt-4 md:mt-0 space-x-2">
                <button
                  onClick={() => handleToggleRole(user.email)}
                  className="bg-blue-600 hover:bg-blue-700 text-white px-3 py-1 rounded"
                >
                  Toggle Role
                </button>
                <button
                  onClick={() => handleDelete(user.email)}
                  className="bg-red-600 hover:bg-red-700 text-white px-3 py-1 rounded"
                >
                  Delete
                </button>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default AdminUsers;
