import { useState } from "react";
import { useNavigate, Link } from "react-router-dom";

const Login = ({ setUserRole }) => {
  const navigate = useNavigate();
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const handleLogin = (e) => {
    e.preventDefault();

    // ✅ Simulated logic — replace with real auth
    const role = email === "admin@example.com"
      ? "admin"
      : email.endsWith("@diaspora.com")
      ? "user"
      : "guest";

    localStorage.setItem("userRole", role);
    localStorage.setItem("userEmail", email);
    setUserRole(role);

    if (role === "admin") navigate("/admin");
    else if (role === "user") navigate("/submit-report");
    else navigate("/");
  };

  return (
    <div className="min-h-screen flex items-center justify-center px-4">
      <div className="bg-white/90 p-8 rounded-xl shadow-md w-full max-w-md text-center">
        <h1 className="text-3xl font-bold text-primary mb-6">Login</h1>
        <form onSubmit={handleLogin} className="space-y-4 text-left">
          <div>
            <label className="label">Email</label>
            <input
              type="email"
              className="input"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
          </div>
          <div>
            <label className="label">Password</label>
            <input
              type="password"
              className="input"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
          </div>
          <button type="submit" className="btn-primary w-full">Login</button>
        </form>

        <div className="text-sm text-gray-600 mt-4">
          <Link to="/signup" className="text-primary hover:underline mr-4">Don't have an account?</Link>
          <span className="text-gray-400">|</span>
          <Link to="#" className="text-primary hover:underline ml-4">Forgot Password?</Link>
        </div>
      </div>
    </div>
  );
};

export default Login;
