import React, { useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";

const Navbar = () => {
  const [role, setRole] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const savedRole = localStorage.getItem("userRole");
    setRole(savedRole);
  }, []);

  const handleLogout = () => {
    localStorage.removeItem("userRole");
    setRole(null); // clear state
    navigate("/"); // redirect to home
  };

  return (
        <nav className="bg-white shadow-md p-4 flex justify-between items-center sticky top-0 z-50">
        <div className="text-2xl font-extrabold text-blue-800 tracking-wide">
          <Link to="/">Alkarama Hub</Link>
        </div>
        <ul className="flex space-x-4 text-sm font-medium">
        <li><Link to="/">Home</Link></li>
        <li><Link to="/reports">Reports</Link></li>
        <li><Link to="/about">About</Link></li>
        {role === "diaspora" && (
          <li><Link to="/submit-report" className="hover:text-blue-500">Submit Report</Link></li>
        )}
        <li><Link to="/register">Register (Diaspora)</Link></li>
        {role === "admin" && <li><Link to="/admin">Admin</Link></li>}
        {!role && (
          <li>
            <Link to="/login" className="text-blue-600 hover:underline">Login</Link>
          </li>
        )}
        {role && (
          <li>
            <button
              onClick={handleLogout}
              className="text-red-600 hover:underline"
            >
              Logout
            </button>
          </li>
        )}
      </ul>
    </nav>
  );
};

export default Navbar;
