import { Link } from "react-router-dom";
import bgImage from "../assets/sudan-flag-bg.jpg";

const projects = [
  {
    id: "clean-up",
    title: "Neighborhood Clean-Up",
    summary: "Empowering locals to revitalize public spaces.",
  },
  {
    id: "youth-tech",
    title: "Youth Tech Mentorship",
    summary: "Free digital literacy and coding workshops for youth.",
  },
  {
    id: "food-solidarity",
    title: "Food Solidarity Drive",
    summary: "Distributing essential supplies to low-income families.",
  },
];

export default function Home() {
  return (
    <div
      className="min-h-screen bg-cover bg-center bg-fixed"
      style={{ backgroundImage: `url(${bgImage})` }}
    >
      <div className="backdrop-blur-sm bg-white/80 pb-20">
        <div className="text-center py-20 px-4">
          <h1 className="text-5xl font-extrabold text-primary tracking-tight mb-4">
            Welcome to Alkarama Hub
          </h1>
          <p className="text-textDark text-xl max-w-2xl mx-auto">
            Grassroots Solutions. Diaspora Impact. Community Power.
          </p>
        </div>

        <div className="max-w-6xl mx-auto px-6">
          <h2 className="section-title">Featured Projects</h2>
          <div className="grid gap-8 md:grid-cols-3">
            {projects.map((project) => (
              <Link
                key={project.id}
                to={`/projects/${project.id}`}
                className="card border-l-4 border-primary hover:scale-105"
              >
                <h3 className="text-lg font-bold text-primary mb-2">
                  {project.title}
                </h3>
                <p className="text-textDark">{project.summary}</p>
                <span className="text-secondary mt-4 inline-block font-medium hover:underline">
                  Learn more â†’
                </span>
              </Link>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
}
